<span class="line">            a3_<span class="token punctuation">{</span>std<span class="token double-colon punctuation">::</span><span class="token generic-function"><span class="token function">forward</span><span class="token generic class-name"><span class="token operator">&lt;</span>T3<span class="token operator">></span></span></span><span class="token punctuation">(</span>t3<span class="token punctuation">)</span><span class="token punctuation">}</span></span>
