<span class="line">        <span class="token keyword">auto</span> t <span class="token operator">=</span> <span class="token generic-function"><span class="token function">make_unique2</span><span class="token generic class-name"><span class="token operator">&lt;</span>B<span class="token operator">></span></span></span><span class="token punctuation">(</span><span class="token number">2</span><span class="token punctuation">,</span> i<span class="token punctuation">,</span> <span class="token number">3</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line">    </span>
