<span class="line">        <span class="token punctuation">[</span><span class="token punctuation">[</span>maybe_unused<span class="token punctuation">]</span><span class="token punctuation">]</span> B b <span class="token operator">=</span> <span class="token function">make_B</span><span class="token punctuation">(</span><span class="token number">4</span><span class="token punctuation">,</span> i<span class="token punctuation">,</span> <span class="token number">5</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
